import BasketItem from "./BasketItem";
import { useContext } from "react";
import { ShopContext } from '../context';

export default function BasketList() {
    let value = useContext(ShopContext);
    let totalPrice = value.order.reduce((accum, item) => accum + item.price * item.quantity, 0);
    return (
        <>
            <ul className="list-group mt-3 basket-list">
                <button onClick={() => value.handleBasketShow()} type="button" className="btn-close basket-list__close fs-5"></button>
                <li className="list-group-item active" aria-current="true">Корзина</li>
                {value.order.length ? value.order.map((item, i) => (
                    <BasketItem key={item.id || i} {...item} />
                )) : <li className="list-group-item">Корзина пустая</li>
                }
                <li className="list-group-item active d-flex justify-content-between mt-auto" aria-current="true">
                    <span>Общая стоимость: {totalPrice}$</span>
                    <button type="button" className="btn btn-success" aria-current="true">
                        Оформить
                    </button>
                </li>
            </ul>
        </>
    )
}
