import { useContext } from "react";
import { ShopContext } from '../context';

export default function BasketItem({ id, name, price, quantity }) {
    const value = useContext(ShopContext);

    return (
        <>
             <li className="list-group-item d-flex justify-content-between align-items-center">
                    <div>{name} <span onClick={() => value.changeQuantityGood(id, 'dec')} className="p-2 basket-list__quantity">-</span>x{quantity}<span onClick={() => value.changeQuantityGood(id, 'inc')} className="p-2 basket-list__quantity">+</span> = {price * quantity}$</div>
                <button type="button" className="btn-close" onClick={() => value.deleteFromBasket(id)}></button>
            </li>
        </>
    )
}
